mapper.select:
SELECT *
FROM PROJECT
WHERE PROJECT_NO = #{projectNo}

mapper.delete:
DELETE FROM INTEREST
WHERE PROJECT_NO = #{projectNo}
AND MEMBER_NO = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM INTEREST
WHERE PROJECT_NO = #{projectNo}
AND MEMBER_NO = #{userNo}

mapper.update:
UPDATE PAYMENT SET PAY_STATUS = #{payStatus}
, COUNT = (SELECT COUNT FROM PAYMENT WHERE PAY_NO = #{payNo}) + 1
WHERE PAY_NO = #{payNo}

mapper.insert:
INSERT INTO
	ATTACHMENT
