	SYSDATE, SEQ_FUNDNO.NEXTVAL, #{userNo}, #{projectNo},
	#{fundMoney}, #{rewardNo}, #{rewardCount}, #{blind}
)

		INSERT INTO
			OPEN_ALRAM
		VALUES(#{memberNo},#{memberPhone},(select project_open 
