		)

mapper.select:
SELECT SUM(FUND_MONEY)
FROM FUND
WHERE PROJECT_NO = #{projectNo}

SELECT COUNT(MEMBER_NO)
 FROM FUND
 WHERE PROJECT_NO = #{projectNo}
