mapper.insert:
INSERT INTO
PAYMENT(PAY_NO, MEMBER_NO, FUND_NO, PAY_STATUS, COUNT, AMOUNT)
VALUES(
#{payNo}, #{userNo}, 3, #{payStatus}, 1, #{amount}
)

mapper.update:
UPDATE PAYMENT SET PAY_STATUS = #{payStatus}
AND COUNT = (SELECT COUNT FROM PAYMENT WHERE PAY_NO = #{payNo}) + 1
WHERE PAY_NO = #{payNo}
